mapper.insert:
	INSERT /*crawler.inserCrawConfig */ INTO craw_config
			(
				siteNm
				,seedURL
				,useYn
				,crawlAgent
				,createNo
				,createDt
				,updateDt
				,updateNo
			) 
		VALUES (
				#{siteNm}
				,#{seedStrURL}
				,#{useYn}
				,#{crawlAgent}
				,#{createNo}
				,#{createDt}
				,#{createDt}
				,#{createNo}
			)
	
        SELECT CONCAT(LAST_INSERT_ID()) as siteConfigSeq

mapper.update:
UPDATE /* crawler.updateCrawConfig */ craw_config 
		SET 
			  createNo =  #{createNo}
			, createDt =  #{createDt}
			, updateNo =  #{createNo}
			, updateDt =  #{createDt}
						, siteNm 	= 	#{siteNm} 		
				, seedURL 	= 	#{seedStrURL}	
						, useYn 	= 	#{useYn}		
				, crawlAgent = #{crawlAgent} 	
		WHERE  siteConfigSeq = #{siteConfigSeq}

mapper.select:
SELECT /*crawler.selectreNewPageInfoMap*/ pageInfoListSeq , siteConfigSeq , goodsNo , goodsNm , goodsPrice , goodsUrl , goodsImg , statCd , failCnt , cate1 , cate2 , cate3 , createNo , createDt , updateDt , updateNo ,'Y' as failYn FROM page_info_list /* where statCd in('01','02') */ where statCd in('99') and failCnt <= 2 and updateDt < DATE_FORMAT(sysdate(), '%Y-%m-%d')
and siteConfigSeq = #{siteConfigSeq}
and pageInfoListSeq = #{pageInfoListSeq}
limit #{rowCnt}

SELECT /*crawler.selectPageInfoMap*/ pageInfoListSeq, siteConfigSeq, goodsNo, goodsNm, goodsPrice, goodsUrl, goodsImg, statCd, failCnt,cate1,cate2,cate3, createNo, createDt, updateDt, updateNo ,'Y' as failYn FROM page_info_list where 1=1 and statCd != '03'
and siteConfigSeq = #{siteConfigSeq}
and pageInfoListSeq = #{pageInfoListSeq}

mapper.insert:

INSERT /*crawler.insertPageInfoList */ INTO page_info_list
	(siteConfigSeq
	, goodsNo
	, goodsNoInt
	, goodsNm
	, goodsPrice
	, goodsUrl
	, goodsImg
	, statCd
	,createNo
	,createDt
	,updateDt
	,updateNo
	) 
VALUES (#{siteConfigSeq}
	, #{goodsNo}
	, #{goodsNoInt}
	, #{goodsNm}
	,#{goodsPrice}
	, #{goodsUrl}
	, #{goodsImg}
	, #{statCd}
	,#{createNo}
	,#{createDt}
	,#{createDt}
	,#{createNo}
	)

mapper.update:
UPDATE /* crawler.updatePageInfoList */ page_info_list
		SET 
			  createNo = createNo
					,  goodsNm = #{goodsNm}	
				, goodsPrice = #{goodsPrice}	
					, goodsUrl = #{goodsUrl}	
					, goodsImg = #{goodsImg}	
				, goodsDisc = #{goodsDisc}	
					,goodsIsbuyPatten= #{goodsIsbuyPatten}	
						,cate1= #{cate1}	
						,cate2= #{cate2}	
						,cate3= #{cate3}	
						, statCd = #{statCd}	
									, failCnt = #{failCnt}	
					, updateDt =  #{updateDt} 	
					, updateNo = #{updateNo}	
	WHERE pageInfoListSeq = #{pageInfoListSeq}
