mapper.select:
select /* ContentDtlComment.xml selectContentDtlComment 2010.12.16*/ cdc.cdc_no , cdc.cd_no , cdc.comment , cdc.use_yn , cdc.mt_no , cdc.create_dt , cdc.create_no , cdc.update_dt , cdc.update_no , mt.mem_nm from content_dtl_comment cdc , mem_tbl mt where cdc.cd_no = #{cd_no} and cdc.use_yn = 'Y' and cdc.mt_no = mt.mt_no
and cdc.cdc_no = #{cdc_no}
and cdc.cdc_no > #{cdc_no_gt}
order by cdc.cdc_no desc

mapper.insert:
			INSERT INTO /* ContentDtlComment.xml insertContentDtlComment 2010.12.16*/ content_dtl_comment(
					cd_no
				,	comment
				,	use_yn
				,	mt_no
				,	create_dt
				,	create_no
					)
			VALUES(
					#{cd_no}
				,	#{comment}
				,	#{use_yn}
				,	#{mt_no}
				,	DATE_FORMAT(#{create_dt},'%Y%m%d%H%i%s')
				,	#{create_no}
					)
    	 select last_insert_id() as cdc_no

mapper.update:
  		update  /* ContentDtlComment.xml updateContentDtlComment 2010.12.16*/  content_dtl_comment set
					update_dt  = DATE_FORMAT(#{create_dt},'%Y%m%d%H%i%s')
					,update_no	= #{create_no}
					
					
						,comment = #{comment}
					
					
					
						,use_yn = #{use_yn}
					
					
  		where  cdc_no = #{cdc_no}
  		
  			and mt_no = #{mt_no}

mapper.insert:
