mapper.select:
select cs_no ,tt_no ,rss2_url ,source_kind ,source_dtl_kind ,source_owner_kind ,source_login_id ,reg_stat ,create_dt ,create_no ,mt_no from content_source_tbl where use_yn = 'Y' and mt_no = #{mt_no}
and tt_no = #{tt_no}
order by source_kind

mapper.insert:
    		
			INSERT INTO content_source_tbl( 
						tt_no
						,mt_no
						,rss2_url
						,source_kind
						,source_dtl_kind
						,source_owner_kind
						,source_login_id
						,reg_stat
						,use_yn
						,create_dt
						,create_no
						,update_dt
						,update_no)
			VALUES(
					#{tt_no}
					,#{mt_no}
					,#{rss2_url}
					,#{source_kind}
					,#{source_dtl_kind}
					,#{source_owner_kind}
					,#{source_login_id}
					,#{reg_stat}
					,'Y'
					,#{create_dt}
					,#{create_no}
					,#{update_dt}
					,#{update_no}
			)
    	
 select last_insert_id() as cs_no

mapper.update:
  		update content_source_tbl set
  							update_dt	= DATE_FORMAT(#{update_dt},'%Y%m%d%H%i%s')
							,update_no	= #{update_no}
							
							
								,rss2_url = #{rss2_url}
							
							
								,source_kind = #{source_kind}
							
							
								,source_dtl_kind = #{source_dtl_kind}
							
							
								,source_owner_kind = #{source_owner_kind}
							
							
								,source_login_id = #{source_login_id}
							
							
								,reg_stat = #{reg_stat}
							
							
								,use_yn = #{use_yn}
							
							
  		where  cs_no = #{cs_no}
