mapper.select:
select /*selectContentTitleListTblList 2010.12.12*/ tt_no ,title_name ,title_kind ,branch_conf_yn ,mt_no ,order_mem_open_yn ,order_mem_join_yn ,order_mem_join_metd ,order_mem_join_passwd ,create_dt ,create_no from content_title_tbl where 1=1 and use_yn = 'Y'
and mt_no = #{mt_no}
and tt_no = #{tt_no}
and 1=2
order by tt_no desc
limit 0,100

mapper.insert:
    		
